import React, { useState, useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { Bar, Line } from "react-chartjs-2";
import { CSVLink } from "react-csv";
import "react-toastify/dist/ReactToastify.css";
import "tailwindcss/tailwind.css";
// Chart.js registration for v3+
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);
// For Excel export
// npm install react-data-export
// import { ExcelFile, ExcelSheet } from "react-data-export";

const API = "http://localhost:8000";

function InventoryApp() {
  // Auth
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isAdmin, setIsAdmin] = useState(false);
  const [isStaff, setIsStaff] = useState(false);

  // Products
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [skeleton, setSkeleton] = useState(false);

  // Add/Edit
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [editing, setEditing] = useState(null);

  // Search/Filter
  const [searchTerm, setSearchTerm] = useState("");
  const [minQty, setMinQty] = useState("");

  // Modal
  const [showConfirm, setShowConfirm] = useState(false);
  const [deleteId, setDeleteId] = useState(null);

  // Low stock
  const [lowStock, setLowStock] = useState([]);

  // AI Forecast
  const [forecast, setForecast] = useState(null);

  // Chart
  const chartData = {
    labels: products.map((p) => p.name),
    datasets: [
      {
        label: "Quantity",
        data: products.map((p) => p.quantity),
        backgroundColor: "rgba(54, 162, 235, 0.6)",
        borderColor: "rgba(54, 162, 235, 1)",
        fill: false,
      },
    ],
  };

  // Auth: login and decode role
  useEffect(() => {
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      try {
        const decoded = jwtDecode(token);
        // Robust role detection: check groups, is_superuser, is_staff
        const groups = decoded.groups || [];
        setIsAdmin(
          (Array.isArray(groups) && groups.includes("admin")) ||
          decoded.is_superuser === true
        );
        setIsStaff(
          (Array.isArray(groups) && groups.includes("staff")) ||
          decoded.is_staff === true
        );
      } catch {
        setIsAdmin(false);
        setIsStaff(false);
      }
    } else {
      setIsAdmin(false);
      setIsStaff(false);
    }
  }, [token]);

  // Ensure Axios always sends the JWT token if present
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${storedToken}`;
    }
  }, []);

  // Fetch products
  const fetchProducts = async (params = {}) => {
    setLoading(true);
    setSkeleton(true);
    try {
      const res = await axios.get(`${API}/products/`, { params });
      setProducts(res.data);
    } catch (err) {
      toast.error("Failed to fetch products");
    }
    setLoading(false);
    setTimeout(() => setSkeleton(false), 800); // skeleton for 0.8s
  };

  // Fetch low stock
  const fetchLowStock = async () => {
    try {
      const res = await axios.get(`${API}/low-stock/?threshold=5`);
      setLowStock(res.data);
      if (res.data.length > 0) {
        toast.warn("Some products are low in stock!");
      }
    } catch {}
  };

  // Fetch AI forecast (placeholder)
  const fetchForecast = async () => {
    // Placeholder: just sum all quantities
    const total = products.reduce((acc, p) => acc + Number(p.quantity), 0);
    setForecast(`Predicted demand next month: ${Math.round(total * 1.1)}`);
  };

  useEffect(() => {
    if (token) {
      fetchProducts();
      fetchLowStock();
    }
    // eslint-disable-next-line
  }, [token]);

  // Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API}/api/token/`, { username, password });
      setToken(res.data.access);
      localStorage.setItem("token", res.data.access);
      toast.success("Login successful!");
    } catch {
      toast.error("Login failed");
    }
  };

  // Logout
  const handleLogout = () => {
    setToken("");
    localStorage.removeItem("token");
    axios.defaults.headers.common["Authorization"] = "";
    setProducts([]);
  };

  // Add or Edit Product
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !quantity || !price) {
      toast.error("All fields are required!");
      return;
    }
    setLoading(true);
    try {
      if (editing) {
        await axios.put(`${API}/products/${editing.id}/`, {
          name,
          quantity: Number(quantity),
          price: Number(price),
          description,
        });
        toast.success("Product updated!");
      } else {
        await axios.post(`${API}/products/`, {
          name,
          quantity: Number(quantity),
          price: Number(price),
          description,
        });
        toast.success("Product added!");
      }
      setName("");
      setQuantity("");
      setPrice("");
      setDescription("");
      setEditing(null);
      fetchProducts();
      fetchLowStock();
    } catch (err) {
      toast.error("Failed to save product");
      console.log(err.response?.data); // Log backend error for debugging
    }
    setLoading(false);
  };

  // Edit
  const startEdit = (product) => {
    setEditing(product);
    setName(product.name);
    setQuantity(product.quantity);
    setPrice(product.price);
    setDescription(product.description);
  };

  // Delete
  const askDelete = (id) => {
    setDeleteId(id);
    setShowConfirm(true);
  };

  const confirmDelete = async () => {
    setShowConfirm(false);
    setLoading(true);
    try {
      await axios.delete(`${API}/products/${deleteId}/`);
      toast.success("Product deleted!");
      fetchProducts();
      fetchLowStock();
    } catch (err) {
      toast.error(
        err.response?.data?.error || "Delete failed (admin only)"
      );
    }
    setLoading(false);
  };

  // Search/filter
  const handleSearch = () => {
    fetchProducts({ name: searchTerm, min_qty: minQty });
  };

  // PDF/CSV/Excel
  const handleDownloadPDF = () => {
    window.open(`${API}/report/pdf/`, "_blank");
  };

  // UI
  if (!token) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-purple-100">
        <form
          onSubmit={handleLogin}
          className="bg-white p-8 rounded-xl shadow-2xl w-96 border border-blue-100"
        >
          <h2 className="text-3xl mb-6 font-extrabold text-blue-700 text-center tracking-tight">Login</h2>
          <input
            className="border border-blue-200 p-3 mb-3 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <input
            className="border border-blue-200 p-3 mb-6 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          <button
            className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white px-4 py-2 rounded-lg w-full font-semibold shadow transition"
            type="submit"
          >
            Login
          </button>
        </form>
        <ToastContainer position="top-center" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 py-8 px-2 sm:px-0">
      <ToastContainer position="top-center" />
      <div className="max-w-5xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-extrabold text-blue-700 tracking-tight drop-shadow">Inventory Management</h1>
          <button
            onClick={handleLogout}
            className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-lg font-semibold shadow"
          >
            Logout
          </button>
        </div>

        {/* Low stock alert */}
        {lowStock.length > 0 && (
          <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-800 p-4 mb-6 rounded shadow flex items-center gap-2">
            <span className="font-bold text-lg">⚠️</span> Some products are low in stock!
          </div>
        )}

        {/* Add/Edit Product */}
        <div className="bg-white rounded-xl shadow-lg p-6 mb-8 border border-blue-100">
          <form
            onSubmit={handleSubmit}
            className="flex flex-col md:flex-row md:items-end gap-4"
          >
            <input
              className="border border-blue-200 p-3 rounded-lg flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              className="border border-blue-200 p-3 rounded-lg flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Quantity"
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
            />
            <input
              className="border border-blue-200 p-3 rounded-lg flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Price"
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <input
              className="border border-blue-200 p-3 rounded-lg flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <button
              className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white px-6 py-3 rounded-lg font-bold shadow transition"
              type="submit"
              disabled={loading}
            >
              {editing ? "Update" : "Add"} Product
            </button>
            {editing && (
              <button
                className="bg-gray-200 hover:bg-gray-300 px-6 py-3 rounded-lg font-bold shadow transition"
                type="button"
                onClick={() => {
                  setEditing(null);
                  setName("");
                  setQuantity("");
                  setPrice("");
                  setDescription("");
                }}
              >
                Cancel
              </button>
            )}
          </form>
        </div>

        {/* Search/Filter */}
        <div className="flex flex-col md:flex-row gap-4 mb-6">
          <input
            className="border border-blue-200 p-3 rounded-lg flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Search by name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <input
            className="border border-blue-200 p-3 rounded-lg flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Min Quantity"
            type="number"
            value={minQty}
            onChange={(e) => setMinQty(e.target.value)}
          />
          <button
            className="bg-green-500 hover:bg-green-600 text-white px-6 py-3 rounded-lg font-bold shadow transition"
            onClick={handleSearch}
          >
            Search
          </button>
          <button
            className="bg-gray-200 hover:bg-gray-300 px-6 py-3 rounded-lg font-bold shadow transition"
            onClick={() => {
              setSearchTerm("");
              setMinQty("");
              fetchProducts();
            }}
          >
            Reset
          </button>
        </div>

        {/* Export/Report */}
        <div className="flex flex-wrap gap-4 mb-6">
          <button
            className="bg-purple-500 hover:bg-purple-600 text-white px-6 py-3 rounded-lg font-bold shadow transition"
            onClick={handleDownloadPDF}
          >
            Download PDF Report
          </button>
          <CSVLink
            data={products}
            filename="products.csv"
            className="bg-green-700 hover:bg-green-800 text-white px-6 py-3 rounded-lg font-bold shadow transition"
          >
            Export CSV
          </CSVLink>
          {/* Excel export placeholder */}
          {/* <ExcelFile element={<button className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded transition">Export Excel</button>} filename="products">
            <ExcelSheet data={products} name="Products" />
          </ExcelFile> */}
        </div>

        {/* AI Forecast */}
        <div className="mb-6">
          <button
            className="bg-indigo-500 hover:bg-indigo-600 text-white px-6 py-3 rounded-lg font-bold shadow transition mb-2"
            onClick={fetchForecast}
          >
            Forecast Demand (AI)
          </button>
          {forecast && <div className="text-indigo-700 font-semibold mt-2">{forecast}</div>}
        </div>

        {/* Charts */}
        <div className="bg-white p-6 rounded-xl shadow-lg mb-8 border border-blue-100">
          <div className="mb-6">
            <Bar data={chartData} />
          </div>
          <div>
            <Line data={chartData} />
          </div>
        </div>

        {/* Product Table */}
        <div className="overflow-x-auto bg-white rounded-xl shadow-lg border border-blue-100">
          <table className="min-w-full text-sm text-left">
            <thead className="bg-blue-100 text-blue-800 uppercase text-xs tracking-wider">
              <tr>
                <th className="py-3 px-4">Name</th>
                <th className="py-3 px-4">Qty</th>
                <th className="py-3 px-4">Price</th>
                <th className="py-3 px-4">Description</th>
                <th className="py-3 px-4">Updated</th>
                <th className="py-3 px-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {skeleton
                ? Array.from({ length: 5 }).map((_, i) => (
                    <tr key={i} className="even:bg-blue-50">
                      <td className="py-3 px-4 bg-gray-200 animate-pulse rounded">&nbsp;</td>
                      <td className="py-3 px-4 bg-gray-200 animate-pulse rounded">&nbsp;</td>
                      <td className="py-3 px-4 bg-gray-200 animate-pulse rounded">&nbsp;</td>
                      <td className="py-3 px-4 bg-gray-200 animate-pulse rounded">&nbsp;</td>
                      <td className="py-3 px-4 bg-gray-200 animate-pulse rounded">&nbsp;</td>
                      <td className="py-3 px-4 bg-gray-200 animate-pulse rounded">&nbsp;</td>
                    </tr>
                  ))
                : products.map((p, idx) => (
                    <tr key={p.id} className="even:bg-blue-50 hover:bg-blue-100 transition">
                      <td className="py-3 px-4 font-semibold text-blue-900">{p.name}</td>
                      <td className="py-3 px-4">{p.quantity}</td>
                      <td className="py-3 px-4">{p.price}</td>
                      <td className="py-3 px-4">{p.description}</td>
                      <td className="py-3 px-4">
                        {p.last_updated ? new Date(p.last_updated).toLocaleString() : ""}
                      </td>
                      <td className="py-3 px-4">
                        <button
                          className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded-lg font-bold shadow mr-2 transition"
                          onClick={() => startEdit(p)}
                        >
                          Edit
                        </button>
                        {isAdmin && (
                          <button
                            className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-lg font-bold shadow transition"
                            onClick={() => askDelete(p.id)}
                          >
                            Delete
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
            </tbody>
          </table>
          {loading && (
            <div className="flex justify-center py-6">
              <div className="w-10 h-10 border-4 border-blue-500 border-dashed rounded-full animate-spin"></div>
            </div>
          )}
        </div>

        {/* Confirm Delete Modal */}
        {showConfirm && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-white p-8 rounded-xl shadow-2xl border border-blue-200">
              <p className="mb-6 text-lg font-semibold text-gray-800">Are you sure you want to delete this product?</p>
              <div className="flex gap-6">
                <button
                  className="bg-red-500 hover:bg-red-600 text-white px-6 py-2 rounded-lg font-bold shadow transition"
                  onClick={confirmDelete}
                >
                  Yes, Delete
                </button>
                <button
                  className="bg-gray-200 hover:bg-gray-300 px-6 py-2 rounded-lg font-bold shadow transition"
                  onClick={() => setShowConfirm(false)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default InventoryApp;
